const Header = (props) => {
	return (
    	<>
			<h1>{props.course}</h1>
    	</>
  	)
}

const Content = (props) => {
	return (
		<>
			<Part part={props.parts[0].name}/>
			<p>
			{props.part2} {props.exercises2}
			</p>
			<p>
			{props.part3} {props.exercises3}
			</p>
		</>
	)
}

const Part = (props) => {
	return (
		<>
			<p>
				{props.part} {props.exercise}
			</p>
		</>
	)
}

const Total = (props) => {
	return (
		<>
			<p>Number of exercises {props.parts[0].exercises + props.parts[1].exercises + props.parts[3].exercises}</p>
		</>
	)
}

const App = () => {
	const course = {
		name: 'Half Stack application development',
		parts: [
		  {
			name: 'Fundamentals of React',
			exercises: 10
		  },
		  {
			name: 'Using props to pass data',
			exercises: 7
		  },
		  {
			name: 'State of a component',
			exercises: 14
		  }
		]
	  }

	return (
		<div>
		<Header course={course.name} />
		<Content parts={course.parts} />
		<Total parts={course.parts} />
		</div>
	)
}

export default App
